INSERT INTO `pbl3test`.`job` (`JOB_ID`, `JOB_NAME`, `COMPANY_ID`, `INDUSTRY`, `LOCATION`, `POSTED_DATE`, `ENROLLMENT_LOCATION`, `ROLE`, `SALARY`, `GENDER_REQUIREMENT`, `NUMBER_OF_RECRUITMENT`, `AGE_REQUIREMENT`, `PROBATION_TIME`, `WORKWAY`, `EXPERIENCE_REQUIREMENT`, `DEGREE_REQUIREMENT`, `BENEFITS`, `JOB_DESCRIPTION`, `JOB_REQUIREMENT`, `DEADLINE`, `SOURCE_PICTURE`) VALUES ('1', 'Nhân Viên Sale Admin', '100', 'Hành chính - Thư ký /Bán hàng - Kinh doanh /Chăm sóc khách hàng', 'Số 1974 Võ Văn Kiệt, Phường An Lạc, Quận Bình Tân, TP  Hồ Chí Minh', '22/04/2024', 'TP.HCM', 'Chuyên viên- nhân viên', '9 - 13 triệu', 'Nữ', '3', '24 - 35 tuổi', '2 tháng', 'Toàn thời gian cố định', '1 năm', 'Cao đẳng', 'Lương thỏa thuận theo năng lực (từ 9 triệu).\nĐược làm việc trong môi trường năng đông, thân thiện và được sự hỗ trợ để phát huy tối đa khả năng của nhân viên.\nĐược đào tạo hằng tuần về kỹ năng chuyên môn tại công ty.\nDo quy mô công ty đang mở rộng nên cơ hội thăng tiến và phát triển công việc lên các vị trí quản lý cao.\nĐược hưởng đầy đủ các quyền lợi theo quy định của luật lao động (BHXH, BHYT, BHTN và 12 ngày phép năm) và các quyền lợi khác theo quy định của nhà nước và thỏa ước lao động của công ty (lương tháng 13, thưởng hiệu quả kinh doanh, lễ, tết,....)', 'Thực hiện các công việc về báo giá và hợp đồng\nTiếp nhận, kiểm tra, tổng hợp các thông tin từ kinh doanh và soạn thảo hồ sơ báo giá, hợp đồng tham khảo, hợp đồng kinh tế và các văn bản khác.\nCập nhật, theo dõi, báo cáo tình hình công nợ và thu hồi công nợ\nPhối hợp với đội ngũ kinh doanh để thu hồi công nợ đầy đủ và đúng hạn, theo dõi và cập nhật tình hình công nợ kịp thời.\nCập nhật và báo cáo chi tiết công nợ hàng tuần, tháng.\nBáo cáo ngay cho cấp quản lý các trường hợp nợ khó đòi hoặc các vấn đề liên quan đến các phòng ban công ty dẫn đến chưa thu hồi công nợ đúng hạn.\nTriển khai và theo dõi quy trình thực hiện hợp đồng kinh tế liên quan đến phần việc của phòng kinh doanh\nTriển khai quy trình thực hiện hợp đồng kinh tế khi có hợp đồng mới đối với tất cả các chi nhánh của công ty.\nTheo dõi quá trình thực hiện, đôn đốc và thông báo cho những người có trách nhiệm liên quan bổ sung các giấy tờ, phụ lục hợp đồng, … đúng quy định của công ty.\nThu thập và kiểm tra các thông tin cần thiết sau khi ký hợp đồng từ nhân viên kinh doanh để gửi cấp quản lý và tính hoa hồng kinh doanh. Tính KPI cho đội ngũ kinh doanh.\nCông việc khác\nLên lịch, chuẩn bị các tài liệu và phối hợp với các phòng ban liên quan cho nhân viên phòng kinh doanh đưa đón khách hàng đi xem thang, về thăm công ty cũng như gặp các đối tác.\nĐề xuất văn phòng phẩm hằng tháng.\nBáo Cáo\nCập nhật tình hình tất cả các chỉ số kinh doanh về kế hoạch và kết quả thực hiện của toàn công ty hàng ngày trên các hệ thống báo cáo. Thực hiện báo cáo tuần, tháng, quý, năm.', 'Nữ, tuổi từ 24\nTốt nghiệp cao đẳng trở lên các ngành có liên quan\nCó trên 1 năm kinh nghiệm ở vị trí tương đương\nCó thể làm việc độc lập và chịu được áp lực công việc\nCó khả năng xử lý tình huống và có trách nhiệm với công việc\nThành thạo vi tính văn phòng\nGiao tiếp tốt, giọng nói dễ nghe', '10/05/2024', 'https://cdn1.vieclam24h.vn/tvn/images/old_employer_avatar/images/970bd85f4ad0762138a40cc9693482c0_5a6fdfa9ac24a_1517281193.jpeg');
